import { Component, OnInit } from '@angular/core';
interface Button {
  text: string;
  color?: 'primary' | 'accent' | 'warn' | string; // Marca color como opcional
}

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css'],

})
export class CalculatorComponent implements OnInit {
  inputValue: string = ''; // Variable que almacena el valor del campo de entrada
  color?: 'primary' | 'accent' | 'warn' | string;

  buttons: Button[][] = [
    [
      { text: '1', color: 'primary' }, { text: '2', color: 'accent' }, { text: '3', color: 'warn' }, { text: '4', color: 'primary' }, { text: '5', color: 'accent' }
    ],
    [
      { text: '6', color: 'warn' }, { text: '7', color: 'primary' }, { text: '8', color: 'accent' }, { text: '9', color: 'warn' }, { text: '0', color: 'primary' }
    ],
    [
      { text: '+', color: 'accent' }, { text: '-', color: 'warn' }, { text: '*', color: 'primary' }, { text: '/', color: 'accent' }, { text: '=', color: 'warn' }
    ]
    // Agrega más filas según sea necesario
  ];

  constructor() { }

  ngOnInit() {
  }
  buttonClick(button: { text: string }) {
    this.inputValue += button.text; 

    console.log(`Clicked: ${button.text}`);
  }


}
<div class="container">
  <input type="text" [value]="inputValue" readonly class="input-field"/>
  <div class="button-grid">
    <ng-container *ngFor="let row of buttons">
      <div class="row">
        <ng-container *ngFor="let button of row">
          <button mat-fab color="primary" (click)="buttonClick(button)" class="button-item">
            {{ button.text }}
          </button>
        </ng-container>
      </div>
    </ng-container>
  </div>
</div>
