import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { IntegralService } from 'src/app/services/integral.service';
import { MatSnackBar } from '@angular/material/snack-bar';

interface Button {
  text: string;
  color?: 'primary' | 'accent' | 'warn' | string;
}

  @Component({
    selector: 'app-calculator',
    templateUrl: './calculator.component.html',
    styleUrls: ['./calculator.component.css'],

  })

export class CalculatorComponent implements OnInit {
cls() {
  this.inputValue =''
}
  inputValue: string = ''; // Variable que almacena el valor del campo de entrada
  color?: 'primary' | 'accent' | 'warn' | string;

  buttons: Button[][] = [
    [{ text: '1' }, { text: '2' }, { text: '3' }, { text: '+', color: 'accent' }, { text: '-', color: 'accent' }],
    [{ text: '4' }, { text: '5' }, { text: '6' }, { text: '*', color: 'accent' }, { text: '/', color: 'accent' }],
    [{ text: '7' }, { text: '8' }, { text: '9' }, { text: '^', color: 'accent' }, { text: '{a}^{b}', color: 'accent' }],
    [{ text: 'nth_root()', color: 'accent' }, { text: 'e' }, { text: 'pi' }, { text: 'sqrt', color: 'accent' }, { text: 'log10', color: 'accent' }],
  ];
  form: FormGroup;


  constructor(private fb: FormBuilder, private integralService : IntegralService,private _snackBar: MatSnackBar) {
    this.form = this.fb.group({
      expression: [''],
      sub_intervals: [''],
      type_method: ['']
    });
  
  }
  
  openSnackBar(message: string, action: string) {
    this._snackBar.open(message, action, {
      duration: 4000, 
    });
  }

  ngOnInit() {
  }
  buttonClick(button: { text: string }) {
    this.inputValue += button.text; 

    console.log(`Clicked: ${button.text}`);
  }
  calculate(){
    this.integralService.save(this.form.value).subscribe(()=>{
      console.log('a')

    },() => {
      this.openSnackBar('asegurate de que el formato LaTeX sea corecto','Cerrar')
    });
  }
  


}

<form class="form" [formGroup]="form">
  <div class="title">Calculadora</div>
  <div class="input-container ic1">
    <input id="integral" formControlName="expression" class="input" type="text" placeholder=" " />
    <div class="cut"></div>
    <label for="integral" class="placeholder">Integral</label>
  </div>
  <div class="input-container ic2">
    <input id="sub_int" formControlName="sub_intervals" class="input" type="text" placeholder=" " />
    <div class="cut" style="width: 100px;"></div>
    <label for="sub_int" class="placeholder">Sub intervalo</label>
  </div>
  <div class="input-container ic2">
    <select id="type_method" name="type_method" class="input" formControlName="type_method">
      <option value="" selected disabled>Tipo de metodo</option>
      <option value="SIMPSON_1_3">SIMPSON_1_3</option>
      <option value="TRAPEZOIDAL_METHOD">TRAPEZOIDAL_METHOD</option>
      <option value="JORGE">JORGE</option>
      <option value="SIMPSON_3_8">SIMPSON_3_8</option>
      <option value="OPEN_SIMPSON">OPEN_SIMPSON</option>
    </select>
  </div>
  <button type="text" class="submit" (click)="calculate()">Calcular</button>
</form>
<div class="keyboard">
  <div class="keyboard-rows r1">
    <button>1</button>
    <button>2</button>
    <button>3</button>

  </div>
</div>
